# @title Default title text
class DecisionTree:
    def __init__(self, max_depth=3):
        """
        Initialize the decision tree.
        :param max_depth: Maximum depth of the tree
        """
        self.max_depth = max_depth
        self.tree = None

    def fit(self, X, y):
        """
        Train the decision tree.
        :param X: Feature matrix
        :param y: Target labels
        """
        self.tree = self._build_tree(X, y, depth=0)

    def predict(self, X):
        """
        Predict labels for the given data.
        :param X: Feature matrix
        :return: Predicted labels
        """
        return np.array([self._traverse_tree(x, self.tree) for x in X])

    def _build_tree(self, X, y, depth):
        """
        Recursively build the decision tree.
        :param X: Feature matrix
        :param y: Target labels
        :param depth: Current depth of the tree
        :return: Tree represented as a dictionary
        """
        # Stop conditions
        #(i.e., all instances belong to the same class), so the algorithm should stop and create a leaf node.
        if len(set(y)) == 1 or depth >= self.max_depth or len(X) == 0:
            return {'leaf': True, 'value': self._majority_class(y)}

        # Find the best split
        best_split = self._find_best_split(X, y)
        if best_split is None:
            return {'leaf': True, 'value': self._majority_class(y)}

        left_idx = best_split['left_idx']
        right_idx = best_split['right_idx']

        # Create subtrees recursively
        return {
            'leaf': False,
            'feature': best_split['feature'],
            'threshold': best_split['threshold'],
            'left': self._build_tree(X[left_idx], y[left_idx], depth + 1),
            'right': self._build_tree(X[right_idx], y[right_idx], depth + 1)
        }

    def _find_best_split(self, X, y):
        """
        Find the best split for the data.
        :param X: Feature matrix
        :param y: Target labels
        :return: Dictionary containing best split
        """
        best_gain = -1
        best_split = None
        print("X.shape[1]", X.shape[1])

        for feature_idx in range(X.shape[1]):
            print("feature_idx", feature_idx)
            print("thresholds  np.unique", X[:, feature_idx])
            thresholds = np.unique(X[:, feature_idx])
            print("thresholds", thresholds)
            for threshold in thresholds:
                left_idx = X[:, feature_idx] <= threshold
                right_idx = X[:, feature_idx] > threshold
                if sum(left_idx) == 0 or sum(right_idx) == 0:
                    continue

                gain = self._information_gain(y, left_idx, right_idx)
                if gain > best_gain:
                    best_gain = gain
                    best_split = {
                        'feature': feature_idx,
                        'threshold': threshold,
                        'left_idx': left_idx,
                        'right_idx': right_idx
                    }
        return best_split

    def _information_gain(self, y, left_idx, right_idx):
        """
        Calculate information gain from a split.
        :param y: Target labels
        :param left_idx: Boolean array for the left split
        :param right_idx: Boolean array for the right split
        :return: Information gain
        """
        def entropy(labels):
            counts = np.bincount(labels)
            probabilities = counts / len(labels)
            return -np.sum([p * np.log2(p) for p in probabilities if p > 0])

        n = len(y)
        parent_entropy = entropy(y)
        left_entropy = entropy(y[left_idx])
        right_entropy = entropy(y[right_idx])

        weighted_avg = (sum(left_idx) / n) * left_entropy + (sum(right_idx) / n) * right_entropy
        return parent_entropy - weighted_avg

    def _majority_class(self, y):
        """
        Return the majority class in the labels.
        :param y: Target labels
        :return: Majority class
        """
        return np.bincount(y).argmax()

    def _traverse_tree(self, x, node):
        """
        Traverse the tree to make a prediction.
        :param x: Single data point
        :param node: Current tree node
        :return: Predicted label
        """
        if node['leaf']:
            return node['value']
        if x[node['feature']] <= node['threshold']:
            return self._traverse_tree(x, node['left'])
        return self._traverse_tree(x, node['right'])

